package com.devlabs.lab1;

public class QuotientReminderAsgn {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=10, b=3, quotient=0, reminder=0;
		quotient=a/b;
		reminder=a%b;
		
		System.out.println("Quotient of first " + a + " and second number " + b + " is " + quotient);
		System.out.println("Reminder of first " + a + " and second number " + b + " is " + reminder);
		
	
	}

}
