package com.devlabs.lab1;

public class UnaryOperatorAsgn {

	public static void main(String[] args) {
		// TODO Auto-generated method stub


		int a=5, b=6;
        
        System.out.println("Pre-increment of a is : " + ++a);
        System.out.println("Before Post-increment of b is : " + b++);
        System.out.println("After Post-increment of b is : " + b);
		
	}

}
